$(document).ready(function(){const e=document.querySelectorAll(".card-item-btn");e.forEach(e=>{e.addEventListener("click",function(){this.classList.toggle("active");const e=this.nextElementSibling,t=this.querySelector(".plus-icon"),n=this.querySelector(".minus-icon");e.style.maxHeight?(e.style.maxHeight=null,t.style.display="block",n.style.display="none"):(e.style.maxHeight=e.scrollHeight+"px",t.style.display="none",n.style.display="block")})})})